import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      num1: 1,
      num2: 1,
      score: 0,
      response: "",
    }

  }
  render() {
    return (
      <div>
        <h1>{this.state.num1} + {this.state.num2}</h1>
        <input value={this.state.response}/>
      </div>
    );
  }
}

export default App;
